(37, 0, 0, 'migxHookAftercollectmigxitems', '', 0, 9, 0, '$configs = $modx->getOption(''configs'', $_REQUEST, '''');\n\n$rows = $modx->getOption(''rows'', $scriptProperties, array());\n$newrows = array();\n\n\nif (is_array($rows)) {\n    $max_id = 0;\n    $dbfields = array();\n    $existing_dbfields = array();\n    foreach ($rows as $key => $row) {\n        if (isset($row[''MIGX_id'']) && $row[''MIGX_id''] > $max_id) {\n            $max_id = $row[''MIGX_id''];\n        }\n        if (isset($row[''selected_dbfields'']) && isset($row[''existing_dbfields''])) {\n            $dbfields = is_array($row[''selected_dbfields'']) ? $row[''selected_dbfields''] : array($row[''selected_dbfields'']);\n            \n            $existing_dbfields = explode(''||'', $row[''existing_dbfields'']);\n            //echo ''<pre>'' . print_r($existing_dbfields,1) . ''</pre>'';die();\n\n        } else {\n            $newrows[] = $row;\n        }\n\n    }\n\n    foreach ($dbfields as $dbfield) {\n        if (!empty($dbfield) && !in_array($dbfield, $existing_dbfields)) {\n            $max_id++;\n            $newrow = array();\n            $newrow[''MIGX_id''] = $max_id;\n\n            switch ($configs) {\n                case ''migxformtabfields'':\n                    $newrow[''field''] = $dbfield;\n                    $newrow[''caption''] = $dbfield;\n                    break;\n                case ''migxcolumns'':\n                    $newrow[''dataIndex''] = $dbfield;\n                    $newrow[''header''] = $dbfield;\n                    break;                    \n            }\n\n\n            $newrows[] = $newrow;\n        }\n    }\n\n\n}\n\n\nreturn json_encode($newrows);', 0, '', '', 0, ''),
(38, 0, 0, 'migxAutoPublish', '', 0, 9, 0, '$classnames = explode('','', $modx->getOption(''classnames'',$scriptProperties,''''));\n$packageName = $modx->getOption(''packageName'',$scriptProperties,'''');\n\nswitch ($mode) {\n    case ''datetime'' :\n        $timeNow = strftime(''%Y-%m-%d %H:%M:%S'');\n        break;\n    case ''unixtime'' :\n        $timeNow = time();\n        break;\n    default :\n        $timeNow = strftime(''%Y-%m-%d %H:%M:%S'');\n        break;\n}\n\n$modx->addPackage($packageName, $modx->getOption(''core_path'') . ''components/'' . $packageName . ''/model/'');\n\nforeach ($classnames as $classname) {\n    if (!empty($classname)) {\n        $tblResource = $modx->getTableName($classname);\n        if (!$result = $modx->exec("UPDATE {$tblResource} SET published=1,publishedon=pub_date,pub_date=null WHERE pub_date < ''{$timeNow}'' AND pub_date > 0 AND published=0")) {\n            $modx->log(modX::LOG_LEVEL_ERROR, ''Error while refreshing resource publishing data: '' . print_r($modx->errorInfo(), true));\n        }\n        if (!$result = $modx->exec("UPDATE $tblResource SET published=0,unpub_date=null WHERE unpub_date < ''{$timeNow}'' AND unpub_date IS NOT NULL AND unpub_date > 0 AND published=1")) {\n            $modx->log(modX::LOG_LEVEL_ERROR, ''Error while refreshing resource unpublishing data: '' . print_r($modx->errorInfo(), true));\n        }\n    }\n\n}\n$modx->cacheManager->refresh();', 0, '', '', 0, ''),
(39, 0, 0, 'JSONtoChunk', 'Custom output filter. If the input is an JSON array it will output the chunk you specified with placeholders for all the values.', 0, 21, 0, '/**\n * JSONtoChunk\n * Custom output filter. If the input is an JSON array it will output the chunk you specified with placeholders for all the values\n *\n * Usage: [[*tvValue:JSONtoChunk=`ChunkName`]]\n * Usage inside chunk: [[+key]]\n *\n * @author Sterc <modx+mapstv@sterc.nl>\n */\n$output = $input;\n$array = $modx->fromJSON($input);\nif (count($array) > 0) {\n    $chunk = $modx->getObject(''modChunk'', array(''name'' => $options));\n    if ($chunk) {\n        $output = $chunk->process($array);\n    }\n}\nreturn $output;', 0, 'a:0:{}', '', 0, ''),
(40, 0, 0, 'getSelections', '', 0, 23, 0, '/**\n * getSelections\n *\n * DESCRIPTION\n *\n * This snippet is a helper for getResources call.\n * It will allows you to select all linked resources under specific Selection with a usage of getResources snippet.\n * Returns distinct list of linked Resources for given Selections\n *\n * getResources are required\n *\n * PROPERTIES:\n *\n * &sortdir                 string  optional    Direction of sorting by linked resource''s menuindex. Default: ASC\n * &selections              string  optional    Comma separated list of Selection IDs for which should be retrieved linked resources. Default: empty string\n * &getResourcesSnippet     string  optional    Name of getResources snippet. Default: getResources\n * &excludeResources        string  optional    Comma separated list of Resources to exclude, even though they are in the Selection\n *\n * USAGE:\n *\n * [[getSelections? &selections=`1` &tpl=`rowTpl`]]\n * [[getSelections? &selections=`1` &tpl=`rowTpl` &sortby=`RAND()`]]\n *\n * @var modX $modx\n * @var array $scriptProperties\n */\n\n$corePath = $modx->getOption(''collections.core_path'', null, $modx->getOption(''core_path'', null, MODX_CORE_PATH) . ''components/collections/'');\n\n/** @var Collections $collections */\n$collections = $modx->getService(\n    ''collections'',\n    ''Collections'',\n    $corePath . ''model/collections/'',\n    array(\n        ''core_path'' => $corePath\n    )\n);\nif (!($collections instanceof Collections)) return '''';\n\n$getResourcesSnippet = $modx->getOption(''getResourcesSnippet'', $scriptProperties, ''getResources'');\n\n$getResourcesExists = $modx->getCount(''modSnippet'', array(''name'' => $getResourcesSnippet));\nif ($getResourcesExists == 0) return ''getResources not found'';\n\n$sortDir = strtolower($modx->getOption(''sortdir'', $scriptProperties, ''asc''));\n$selections = $modx->getOption(''selections'', $scriptProperties, '''');\n$sortBy = $modx->getOption(''sortby'', $scriptProperties, '''');\n\n$selections = $collections->explodeAndClean($selections);\n\nif ($sortDir != ''asc'') {\n    $sortDir = ''desc'';\n}\n\n$linkedResourcesQuery = $modx->newQuery(''CollectionSelection'');\n\nif (!empty($selections)) {\n    $linkedResourcesQuery->where(array(\n        ''collection:IN'' => $selections\n    ));\n}\n\nif ($sortBy == '''') {\n    $linkedResourcesQuery->sortby(''menuindex'', $sortDir);\n}\n\n$linkedResourcesQuery->select(array(\n    ''resource'' => ''DISTINCT(resource)''\n));\n\n$linkedResourcesQuery->prepare();\n\n$linkedResourcesQuery->stmt->execute();\n\n$linkedResources = $linkedResourcesQuery->stmt->fetchAll(PDO::FETCH_COLUMN, 0);\n\n$excludedResources = $modx->getOption(''excludeResources'', $scriptProperties, '''');\n$excludedResources = $collections->explodeAndClean($excludedResources);\n\nif (!empty($excludedResources)) {\n    $linkedResources = array_diff($linkedResources, $excludedResources);\n}\n\n$linkedResources = implode('','', $linkedResources);\n\n$properties = $scriptProperties;\nunset($properties[''selections'']);\n\n$properties[''resources''] = $linkedResources;\n$properties[''parents''] = ($properties[''getResourcesSnippet''] == ''pdoResources'') ? 0 : -1;\n\nif ($sortBy == '''') {\n    $properties[''sortby''] = ''FIELD(modResource.id, '' . $linkedResources . '' )'';\n    $properties[''sortdir''] = ''asc'';\n}\n\nreturn $modx->runSnippet($getResourcesSnippet, $properties);', 0, 'a:0:{}', '', 0, '');

